var globalData = {
                                "Source" : "aaaw",
				"RemindMeIconUrl" : "http://www.azigo.com/images/rm/crd/aaa_18x24.png",
                                "FlyoutIconUrl" : "http://www.azigo.com/images/rm/crd/aaa_60x90.png"
			 };

	function remindMeSelector(obj) { 
                var annotationContent;
		var remindMeDomain = obj.name.replace(/http:\/\/([A-Za-z0-9.-]+)\/.*/,"$1");
        	remindMeDomain = remindMeDomain.replace("http://","");
		remindMeDomain = remindMeDomain.replace("www.","");
		remindMeDomain = remindMeDomain.replace("www1.","");
		remindMeDomain = remindMeDomain.replace(/\./, "");
		remindMeDomain = remindMeDomain.replace(/\.[^.]+$/,"");
                remindMeDomain = remindMeDomain.replace(/[&]/g,"and");
                remindMeDomain = remindMeDomain.replace(/\s+/g,"");
		remindMeDomain = remindMeDomain.replace(/[']/g,"");
		remindMeDomain = remindMeDomain.replace(/[-]/g,"");
                remindMeDomain = remindMeDomain.toLowerCase();

                var remindMeDivId = "remindMe_"+remindMeDomain;
                var remindMeFlyoutDivId = "remindMeFlyout_"+remindMeDomain;
		var remindMeWrapper = "remindMeWrapper_"+remindMeDomain;

		if($K("#"+remindMeDivId).length == 0) {
		   var remindMeMainDiv = createRemindMeDiv(remindMeDivId);
                   var remindMeFlyoutDiv = createRemindMeFlyoutDiv(remindMeFlyoutDivId);

		   remindMeFlyoutDiv.append(
			getFlyoutDetails(
                                                 obj.name,
                                                 obj.link, 
                                                 globalData.FlyoutIconUrl, 
                                                 obj.text, 
                                                 obj.icon
                                          )
		   );

 	           var remindMeDiv = $K("<div></div>");

                   var wrapperDiv = $K("<div id='"+remindMeWrapper+"' class='remindme-reminders-wrapper'></div>");
		   wrapperDiv.append(remindMeMainDiv);

		   remindMeDiv.append(wrapperDiv);

	           remindMeDiv.append(remindMeFlyoutDiv);

                   annotationContent = remindMeDiv;
                   registerEvents(remindMeDivId, remindMeFlyoutDivId, false);
                }
                else { 
                   if($K("#"+remindMeDivId).find("#img_"+globalData.Source+"_remindMe").length) {
                        return false;
                   }

		   if($K("#"+remindMeDivId).children(".txt-reminder").length) {
                      $K("#"+remindMeDivId).children(".txt-reminder").after(
                                              makeListItem(
		                                    null, 
		                                    null, 
		                                    $K("<img id='img_"+globalData.Source+"_remindMe' src='"+globalData.RemindMeIconUrl+"' />")
		                              )
                      );
                   }

		   if($K("#"+remindMeFlyoutDivId).length) {
			$K("#"+remindMeFlyoutDivId).append(
			                   getFlyoutDetails(
                                                                obj.name,
                                                                obj.link, 
                                                                globalData.FlyoutIconUrl, 
                                                                obj.text, 
                                                                obj.icon
                                           )
		        );
  		   }

		   //Increment the counter.
		   var spanReminders = $K("#"+remindMeDivId).children(".txt-reminder").children(".spanRemindMeNReminders");
                   if (spanReminders.length > 0)
                   {
                       var totalReminders = parseInt(spanReminders.text());
                       if (!isNaN(totalReminders))
                       {
	                    totalReminders = totalReminders + 1;
                            spanReminders.text(String(totalReminders));
	                    if(totalReminders == 1) {
		               $K("#"+remindMeDivId).children(".txt-reminder").children(".spanRemindMeTextReminders").text("Reminder");
	                    }
	                    else {
		               $K("#"+remindMeDivId).children(".txt-reminder").children(".spanRemindMeTextReminders").text("Reminders");
	                    }
                       }                
		   }
                   annotationContent = false;
                }
	     return annotationContent;
        }

        function registerEvents(remindMeDivId, remindMeFlyoutDivId, isSpotlightEvent) {		
	     $K("#"+remindMeDivId).live('mouseover', function () {
		   $K('#'+remindMeDivId).css({'cursor':'hand','cursor':'pointer'});
                   $K('#'+remindMeFlyoutDivId).show();
	     });

	     $K("#"+remindMeDivId).live('mouseout', function () {

                   if(isSpotlightEvent) {
                      $K("#spotlight-reminders-wrapper").live('mouseover', function() {
                           $K('#'+remindMeFlyoutDivId).show();                           
                      });
                      $K("#spotlight-reminders-wrapper").live('mouseout', function() {
                           $K('#'+remindMeFlyoutDivId).hide();   
			   $K("#spotlight-reminders-wrapper").die('mouseover');
			   $K("#spotlight-reminders-wrapper").die('mouseout');
                      });
                   }
		   else {
			$K("#"+remindMeDivId).parent().mouseover(function() {
                           $K('#'+remindMeFlyoutDivId).show();    
			});
                      $K("#"+remindMeDivId).parent().mouseout(function() {
                           $K('#'+remindMeFlyoutDivId).hide();   
			   $K("#"+remindMeDivId).parent().unbind('mouseover');
			   $K("#"+remindMeDivId).parent().unbind('mouseout');
                      });
		   }

		   $K('#'+remindMeFlyoutDivId).hide();
	     });

	     $K("#"+remindMeFlyoutDivId).live('mouseover', function () {
        	   $K('#'+remindMeFlyoutDivId).show();
	     });

	     $K("#"+remindMeFlyoutDivId).live('mouseout', function () {
		   $K('#'+remindMeFlyoutDivId).hide();
		   if(isSpotlightEvent) {
			   $K("#spotlight-reminders-wrapper").die('mouseover');
			   $K("#spotlight-reminders-wrapper").die('mouseout');
		   }
		   else {
			   $K("#"+remindMeDivId).parent().unbind('mouseover');
			   $K("#"+remindMeDivId).parent().unbind('mouseout');
		   }
             });
        }

	function createRemindMeDiv(remindMeDivId) {
             var remindMeMainUl = $K("<ul></ul>");
             remindMeMainUl.attr({"id":remindMeDivId, "class":"spotlightReminders"});

             remindMeMainUl.append(
                  makeListItem(null, "azigo-logo", $K("<img src='http://www.azigo.com/images/rm/azigo_16x16.png' />"))
             );

             remindMeMainUl.append(
                  makeListItem(
		     "remindme-txt-reminder", 
		     "txt-reminder", 
		     "<span class='spanRemindMeNReminders'>1</span> <span class='spanRemindMeTextReminders'>Reminder</span>"
		  )
             );

             remindMeMainUl.append(
                  makeListItem(
		     null, 
		     null, 
		     $K("<img id='img_"+globalData.Source+"_remindMe' src='"+globalData.RemindMeIconUrl+"' />")
		  )
             );

             remindMeMainUl.append(
                  makeListItem(null, null, $K("<img src='http://www.azigo.com/images/rm/FlyoutIndicator.png' />"))
             );

             return remindMeMainUl;
        }

	function createRemindMeFlyoutDiv(remindMeFlyoutDivId)
        {
	    var remindMeFlyoutDiv = $K("<div></div>");
	    remindMeFlyoutDiv.attr({"id":remindMeFlyoutDivId, "class":"remindme-flyout-wrapper"});
	    remindMeFlyoutDiv.append($K("<div></div>").attr("class", "flyout-pointer"));
            return remindMeFlyoutDiv;
        }

	function makeListItem(listItemId, listItemClass, listItemContent) {
             var listItem = $K("<li></li>");
             if(listItemClass != null) {
                 listItem.attr("class", listItemClass);
             }
	     if(listItemId != null) {
	         listItem.attr("id", listItemId);
	     }
             listItem.append(listItemContent);
             return listItem;        
        }

	function makeAnchorTag(aUrl, aClass, aText)
        {
             var anchorTag = $K("<a></a>");
             anchorTag.attr("href", aUrl);
             if(aClass != null) {
                 anchorTag.attr("class", aClass);
             }
             anchorTag.append(aText);
             return anchorTag;
        }

    /*
	Function that takes the details to be displayed in the flyout and creates a flyout component with those details.
    */
    function getFlyoutDetails(clientName, clientUrl, clientLogo, displayText, buttonType)
    { 	         
	var flyoutDetailsDiv = $K("<div></div>");
	flyoutDetailsDiv.attr("class","flyout-reminder-details clearfix");

        var flyoutDetailsUl = $K("<ul></ul>");

        flyoutDetailsUl.append(
		makeListItem(null, null, $K("<img src='"+clientLogo+"' />"))
	);
        flyoutDetailsUl.append(
                makeListItem(null, "flyout-reminder-url", makeAnchorTag(clientUrl, null, clientName))
        );
        flyoutDetailsUl.append(
		makeListItem(null, "flyout-reminder-text", displayText)
	);
	var discountButton = "";
	//if(buttonType === "discount") {
		discountButton = makeAnchorTag(clientUrl, "flyout-reminder-button", "Click and Save");
	//}
        flyoutDetailsUl.append(
		makeListItem(null, null, discountButton)
	);        

        flyoutDetailsDiv.append(flyoutDetailsUl);

	return flyoutDetailsDiv;
    }

/*
    Main function for the Spotlight that will be called from each rule.
*/
KOBJ.spotlight = function (source) {
    /*
	Callback function to handle JSON data returned from the nutchfilter url.
    */
    function datasetcallback(d){
        if(d) {
           var response = d.response;
           if(response) {
               if(response.docs.length > 0) {
               	    displaySpotlight(response);
               }
           }
        }      	
    }

    /* 
	Take out the search string from the url and construct a remoteUrl for the current source. 
    */

    var q = String(window.location).replace(/^.*[\?&][qp]=([^&]+).*$/, "$1");
    var remoteUrl = "http://service.azigo.com/solr/nutchfilter.jsp?q="+q+"&fq=source:"+source+"&callback=?";

    $K.getJSON(remoteUrl,datasetcallback);

    /*
	Function to create the main div for Spotlight. It just creates the spotlight template that will be displayed 
	above the search results.
    */
    function createSpotlightMainDiv()
    {
	 var spotlightMainDiv = $K("<div></div>");
	 spotlightMainDiv.attr("id", "spotlight-reminders-wrapper");

         var spotlightMainUl = $K("<ul></ul>");
         spotlightMainUl.attr({"id":"spotlightReminders", "class":"spotlightReminders"});

         spotlightMainUl.append(
                    makeListItem(null, 'azigo-logo', $K("<img src='http://www.azigo.com/images/rm/azigo_16x16.png' />"))
         );

         var spotlightReminderNSpan = $K("<span></span>");
         spotlightReminderNSpan.attr("id", "spanNReminders");
         spotlightReminderNSpan.text("0");

         var spotlightReminderTextSpan = $K("<span></span>");
         spotlightReminderTextSpan.attr("id", "spanTextReminders");
         spotlightReminderTextSpan.text("Reminders");

         spotlightMainUl.append(
                           makeListItem(
				"spotlight-txt-reminder", 
				"txt-reminder", 
				"<span id='spanNReminders'>0</span> <span id='spanTextReminders'>Reminders</span>"
			   )
         );

         spotlightMainUl.append(
                    makeListItem(null, null, $K("<img src='http://www.azigo.com/images/rm/FlyoutIndicator.png' />"))
         );

         var spotlightMainP = $K("<p></p>");
         spotlightMainP.attr("class", "descriptive-text");
         spotlightMainP.text("My Sponsored Links");

         spotlightMainDiv.append(spotlightMainUl);
         spotlightMainDiv.append(spotlightMainP);

         return spotlightMainDiv;
    }

    /*
	Function to create the Flyout Div for Spotlight. It just creates the flyout template that will be displayed
	once user hovers over the Spotlight components.
    */
    function createSpotlightFlyoutDiv()
    {
	var spotlightFlyoutDiv = $K("<div></div>");
	spotlightFlyoutDiv.attr({"id":"spotlightFlyoutDiv", "class":"flyout-wrapper"});
	spotlightFlyoutDiv.append($K("<div></div>").attr("class", "flyout-pointer"));
        return spotlightFlyoutDiv;
    }

    /*
	Callback function to handle JSON data returned from fetch url - which gives details requierd for the flyout.
    */
    function flyoutContentCallback(data)
    {
        if($K('#spotlightFlyoutDiv').length) {
           $K.each(data, function(){
                        $K('#spotlightFlyoutDiv').append(
			                  getFlyoutDetails(
                                                 this.name,
                                                 this.link, 
                                                 globalData.FlyoutIconUrl, 
                                                 this.text, 
                                                 this.icon
                                          )
                        );
                    }
           );
        }
    }    

    /*
	Main function that creates all the components required for Spotlight and injects them in the search page.
    */
    function displaySpotlight(response)
    {
        var logoUrl = globalData.RemindMeIconUrl;

        if($K("#spotlight-reminders-wrapper").length == 0) {
	    var spotlightMainDiv = createSpotlightMainDiv();
            var spotlightFlyoutDiv = createSpotlightFlyoutDiv();
 	    var spotlightDiv = $K("<div id='spotlight-main-flyout'></div>");
	    spotlightDiv.append(spotlightMainDiv);
	    spotlightDiv.append(spotlightFlyoutDiv);
            if($K("#res").length) {
                $K("#res").prepend(spotlightDiv);
            }
            else if($K("#web").length) {
                $K("#web").prepend(spotlightDiv);
            }
            else if($K("#results").length) {
                $K("#results").prepend(spotlightDiv);
            }
        }

        //Register the mousover and mouseout events to show/hide the flyout.
	registerEvents("spotlightReminders", "spotlightFlyoutDiv", true);

	/*
	    If the details for this source has already been injected in the page, we don't need to go further.
	*/
    	if($K("#img_"+source+"_spotlight").length) {
            return;
        }

        if($K("#spotlight-txt-reminder").length) {
             $K("#spotlight-txt-reminder").after("<li><img id='img_"+source+"_spotlight' src='"+logoUrl+"' /></li>");
        }
        var spanReminders = $K("#spanNReminders");
        if (spanReminders.length > 0)
        {
            var totalReminders = parseInt(spanReminders.text());
           if (!isNaN(totalReminders))
           {
	       if(response.docs.length > 3) {
		   totalReminders = totalReminders + 3;
	       }
	       else {
               	   totalReminders = totalReminders + response.docs.length;
	       }
               spanReminders.text(String(totalReminders));
	       if(totalReminders == 1) {
		   $K("#spanTextReminders").text("Reminder");
	       }
	       else {
		   $K("#spanTextReminders").text("Reminders");
	       }
           }
        }

        /*
            Make a call to the URL which will get us the detailed content as below.
        */
	var jsonData = "";
	var index = 1;
	$K.each(response.docs, function(){
				if(index > 1)
				    jsonData += ",";
				jsonData += "'KOBJL"+index+"':{'url':'"+this.url+"'}";
				index++;
				if(index > 3)
				   return false;
			});

        var jsonUrl = "https://service.azigo.com/remindmeac/fetch?callback=?&jsonData=true&source="+source;
        $K.getJSON(jsonUrl, "annotatedata={"+jsonData+"}", flyoutContentCallback);
  }
};

var url_prefix = "http://frag.kobj.net/clients/1024/images/";

var link_text = {
	"ebates": "<img style='padding-top: 7px;height:28px' src='" + url_prefix + "ebates_logo_34.png' border='0'>"
};


function make_selector(key){
	var func = function(obj){
	  function mk_anchor (o, key) {
	    return $K('<a href=' + o.link + '/>').attr(
	      {"class": 'KOBJ_'+key,
	       "title": o.text || "Click here for discounts!"
	      }).html(link_text[key]);
	  }
	  var entryURL = $K(obj).find("span.url, cite").text();
	  var host = KOBJ.get_host(entryURL);
	  var o = KOBJ.pick(KOBJ['data'][key][host]);
	  //try prepending a www. if necessary
	  if(!o){
	  	o = KOBJ.pick(KOBJ['data'][key]["www." + host]);
	  	//if(o) console.log("www. added, found match");
	  }

          if(!o){
                o = KOBJ.pick(KOBJ['data'][key][host.replace(/^www./, "")]);
          }

	  if(o) {
	     return mk_anchor(o,key);
	  } else {
	    return false;
	  }
	};
	return func;
}

